userDefinedLinkHintCss:
.vimiumHintMarker.internal-vimium-hint-marker {
  /* linkhint boxes */
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#222222af),
    color-stop(100%,#555555af)) !important;
  outline: 3px solid #FFC54277 !important;
  border-radius: 4px !important;
  padding: 3px !important;
  transform: translate(-15%, -15%) !important;
  box-shadow: 0 0 10px 5px #777 !important;
}

.vimiumHintMarker.internal-vimium-hint-marker span {
/* linkhint text */
  color: #ffffff !important;
  font-weight: bold !important;
  font-size: 14px !important;
  text-shadow: 0 0 5px #000 !important;
}

.vimiumHintMarker.internal-vimium-hint-marker > .matchingCharacter {
  color: #FFC542ff !important;
  text-shadow: 0 0 5px #000 !important;
}
